// script.js
document.addEventListener("DOMContentLoaded", () => {
    const searchInput = document.getElementById("search");
    const restaurantsContainer = document.getElementById("restaurants");
    const restaurantDetailsSection = document.getElementById("restaurant-details");

    const restaurantData = [
        {
            name: "The Food Place",
            image: "data:image/jpeg;base64,",
            description: "A cozy place to enjoy delicious food with friends and family.",
            rating: 4.5,
            location: "New York, NY"
        },
        {
            name: "Pizza Palace",
            image: "https://media.istockphoto.com/id/1377372234/photo/pizza-with-salami-bell-pepper-tomatoes-and-cheese-pickles-bacon-and-sausages-on-a-light.jpg?s=612x612&w=0&k=20&c=Cw2GlLY474sFLmBm4IFsSoSXV4wHZM-ub691aaDqLlw=",
            description: "Best pizzas in town, with a variety of toppings.",
            rating: 4.7,
            location: "Los Angeles, CA"
        },
        {
            name: " Bawachi biryani",
            image: "data:image/jpeg;base64,",
            description: "Fresh and authentic sushi, served with a smile.",
            rating: 4.3,
            location: "San Francisco, CA"
        },
        {
            name:"dosa",
            image:"data:image/jpeg;base64,",
            description:"good taste",
            rating:4.3,
            location: "KBHP colony hyderabad",
        }

        // Add more restaurant data as needed
    ];

    const displayRestaurants = (restaurants) => {
        restaurantsContainer.innerHTML = "";
        restaurants.forEach(restaurant => {
            const restaurantElement = document.createElement("div");
            restaurantElement.classList.add("restaurant");
            restaurantElement.innerHTML = `
                <img src="${restaurant.image}" alt="${restaurant.name}">
                <div class="restaurant-info">
                    <h3>${restaurant.name}</h3>
                    <p>Rating: ${restaurant.rating} | Location: ${restaurant.location}</p>
                </div>
            `;

            restaurantElement.addEventListener("click", () => {
                showRestaurantDetails(restaurant);
            });

            restaurantsContainer.appendChild(restaurantElement);
        });
    };

    const showRestaurantDetails = (restaurant) => {
        restaurantDetailsSection.style.display = "block";
        restaurantDetailsSection.innerHTML = `
            <h3>${restaurant.name}</h3>
            <p>${restaurant.description}</p>
            <p><strong>Location:</strong> ${restaurant.location}</p>
            <p><strong>Rating:</strong> ${restaurant.rating}</p>
        `;
    };

    // Search functionality
    searchInput.addEventListener("input", (e) => {
        const query = e.target.value.toLowerCase();
        const filteredRestaurants = restaurantData.filter(restaurant =>
            restaurant.name.toLowerCase().includes(query) ||
            restaurant.description.toLowerCase().includes(query)
        );
        displayRestaurants(filteredRestaurants);
    });

    // Initial display
    displayRestaurants(restaurantData);
});
